mapper.select:
SELECT create_time
FROM sys_sms
WHERE mobile_phone = #{param1}
  AND create_time BETWEEN #{param2} AND #{param3}
ORDER BY create_time DESC

SELECT create_time
FROM sys_sms
WHERE mobile_phone = #{param1}
  AND code = #{param2}
  AND sms_status = 1
  AND sms_type = #{param3}
ORDER BY create_time DESC
LIMIT 0, 1

mapper.update:
UPDATE sys_sms
SET sms_status  = 0,
    update_time = #{param2}
WHERE mobile_phone = #{param1}
